import React, {useState} from 'react';
import Card from '../UI/Card';
import Button from '../UI/Button';
import ErrorModal from '../UI/ErrorModal';
import styles from './AddUser.module.css';


function AddUser(props){

    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [error, setError] = useState();

    function usernameChangeHandler(event){
        setEnteredUsername(event.target.value);
    }

    function ageChangeHandler(event){
        setEnteredAge(event.target.value);
    }


    function addUserHandler(event){
        
        //Allows webpage to not reload
        event.preventDefault(); 
        
        //Print Nothing if Fields Are Empty
        if(enteredUsername.trim().length === 0 || enteredAge.trim().length === 0){
            
            setError({
                title: 'Invalid Input',
                message: 'Please enter a valid name and age (non-empty values)'
            });

            return;
        }
        
        //Print Nothing if Age is Less Than 1
        if(+enteredAge < 1){

            setError({
                title: 'Invalid Age',
                message: 'Please enter a Valid Age (positive integers only)'
            });

            return;
        }

        //Passing Compiled Data to onAddUser in App.js
        props.onAddUser(enteredUsername, enteredAge);

        //Clears Field Once Submitted
        setEnteredUsername('');
        setEnteredAge('');


    };



return( 
<div>
   {error && <ErrorModal title={error.title} message={error.messaage}/>}
    <Card className={styles.input}> 
    <form onSubmit={addUserHandler}>
        
        <label htmlFor="username">Username </label>
        <input 
            id="username" 
            type="text"
            value={enteredUsername}
            onChange={usernameChangeHandler}/>

        <label htmlFor="age"> Age (Years) </label>
        <input 
            id="age" 
            type="number"
            value={enteredAge} 
            onChange={ageChangeHandler}/>
        <Button type="submit">Add User</Button>
    </form>
    </Card>
</div>
    );
}

export default AddUser;
